'use client';

import * as React from 'react';
import { motion, AnimatePresence, type Transition } from 'motion/react';

import {
  useIsInView,
  type UseIsInViewOptions,
} from '@/components/animate-ui/hooks/use-is-in-view';

type RotatingTextProps = Omit<React.ComponentProps<'span'>, 'children'> & {
  text: string | string[];
  duration?: number;
  transition?: Transition;
  y?: number;
  delay?: number;
} & UseIsInViewOptions;

function RotatingText({
  ref,
  text,
  y = -50,
  duration = 2000,
  transition = { duration: 0.3, ease: 'easeOut' },
  delay = 0,
  style,
  inView = false,
  inViewMargin = '0px',
  inViewOnce = true,
  ...props
}: RotatingTextProps) {
  const { ref: localRef, isInView } = useIsInView(
    ref as React.Ref<HTMLElement>,
    { inView, inViewOnce, inViewMargin },
  );

  const start = React.useMemo(
    () => (isInView && inView) || !inView,
    [isInView, inView],
  );

  const [index, setIndex] = React.useState(0);

  React.useEffect(() => {
    if (!Array.isArray(text) || !start) return;

    const timeoutId = setTimeout(() => {
      const intervalId = setInterval(() => {
        setIndex((prev) => (prev + 1) % text.length);
      }, duration);

      (timeoutId as unknown as { intervalId?: NodeJS.Timeout }).intervalId =
        intervalId;
    }, delay * 1000);

    return () => {
      clearTimeout(timeoutId);
      const { intervalId } = timeoutId as unknown as {
        intervalId?: NodeJS.Timeout;
      };
      if (intervalId) clearInterval(intervalId);
    };
  }, [text, duration, delay, start]);

  const currentText = Array.isArray(text) ? text[index] : text;

  return (
    <span
      ref={localRef}
      style={{
        display: 'inline-block',
        padding: '0.25rem 0',
        ...style,
      }}
      {...props}
    >
      <AnimatePresence mode="wait">
        {start && (
          <motion.span
            key={currentText}
            initial={{ opacity: 0, y: -y }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y }}
            transition={{
              ...transition,
              delay: (transition?.delay ?? 0) + delay,
            }}
          >
            {currentText}
          </motion.span>
        )}
      </AnimatePresence>
    </span>
  );
}

export { RotatingText, type RotatingTextProps };
